import React, { useRef, useState } from "react";
import "./App.css";

function App() {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const streamUrl = "https://your-radio-stream-url.com/stream"; // Replace with actual stream URL

  const togglePlayback = () => {
    const audio = audioRef.current;
    if (audio.paused) {
      audio.play();
      setIsPlaying(true);
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1>x Community Radio Station</h1>
        <div className="live-badge">LIVE</div>
        <audio ref={audioRef} src={streamUrl}></audio>
        <button onClick={togglePlayback} className="play-button">
          {isPlaying ? "Pause" : "Play"}
        </button>
      </div>
    </div>
  );
}

export default App;
