// src/components/Footer.js
import React from 'react';
import './Footer.css';
import logo from '../components/assets/logo.jpg'; 
const Footer = () => {
  return (
    <footer className="footer">
  <div className="footer-left">
    <p>  <i class="fas fa-broadcast-tower"></i><strong>About voice Fm</strong></p>
    <p>Voice FM is a commercial radio station that was established in 2024. </p>
    <p>Tshitereke village(Vhembe District) and Western cape</p>
    <p>Makerting contacts 0728099123</p>
  </div>

  <div className="footer-center">
  <img src={logo} alt="Logo"  className="footer-logo" />
    <p>© 2025 Voice FM 1105. All rights reserved.</p>
  </div>

  <div className="footer-right">
    <a href="https://wa.me/0725173730" target="_blank" rel="noopener noreferrer">
      <i className="fab fa-whatsapp"></i>
    </a>
    <a href="tel:+2272 517 3730">
      <i className="fas fa-phone-alt"></i>
    </a>
  </div>
</footer>

  );
};

export default Footer;
